package secondtask;

public class SecondTaskMultiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

// first loop decides the which number we are at 
for(int i = 1;i<=10;i++) { 
	
// second loop does the multiplication 
	for(int j=1;j<=i;j++) {
		System.out.print(i*j+".");	
	}
	System.out.print("\n");
}
	}

}
